mapper.select:
select sum(in_use_driver_num) as inUseDriverNum
,sum(operation_verify_driver_day) as operationVerifyDriverDay
,sum(finish_cl_order_num) as finishClOrderNum
,sum(responsible_complaint_num) as responsibleComplaintNum
from bi_driver_measure_day

 data_date between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}

      and supplier_id in (${supplierIds})


      and supplier_id = #{supplierId}


    and team_id = #{teamId}

select sum(b.disinfect_driver_cnt)/sum(a.in_use_driver_num) penetrance from (select data_date, supplier_id, sum(in_use_driver_num) as in_use_driver_num from bi_driver_measure_day where data_date between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}
and supplier_id = #{supplierId}
and supplier_id in (${suppliers})
)a left join (select data_date, supplier_id, sum(disinfect_driver_cnt) as disinfect_driver_cnt from bi_driver_disinfect_measure_day where data_date between #{startDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR}
and supplier_id = #{supplierId}
and supplier_id in (${suppliers})
)b on a.data_date = b.data_date and a.supplier_id = b.supplier_id group by a.data_date, a.supplier_id
