SELECT driver_id driverId, driver_name driverName, license_plates vehiclePlateNo, evaluate_score driverScore, driver_city_name driverCityName, evaluate_date evaluateTime, case when driver_type = '5' then '自营' when driver_type is null then '' else '加盟' end as driverTypeName, supplier_name allianceName, team_name motorcardName, driver_group_name className, order_no orderNo, evaluate driverEvaluateText, memo driverEvaluateRemark, customer_id evaluateCustomerId, class_name memberRankName, customer_name customerName, CASE order_source WHEN 1 THEN '个人' WHEN 2 THEN '渠道' WHEN 3 THEN '机构' WHEN 4 THEN '酒店' END orderSourceName, fact_date actualAboardTime, fact_end_date completeTime, fact_start_addr actualOnboardLocation, fact_end_addr actualDebusLocation, platform_evaluate appScore, platform_evaluate_score appEvaluateText, platform_memo appRemark, platform osName, sys_version osVersion, superintend_no supervisorNo FROM ${tableName}
AND driver_group_id = #{classId}
AND driver_type = #{driverTypeId}
AND evaluate_score = #{driverScore}
AND platform_evaluate = #{appScore}
and supplier_id = #{allianceId}
and supplier_id in
#{item}
and supplier_id = #{allianceId}
and team_id = #{motorcadeId}
and team_id in
#{item}
and team_id = #{motorcadeId}
AND order_city_id = #{orderCityId}
AND order_city_id in
#{id}
AND order_city_id = #{orderCityId}

SELECT driver_id driverId, driver_name driverName, license_plates vehiclePlateNo, evaluate_score driverScore, driver_city_name driverCityName, evaluate_date evaluateTime, case when driver_type = '5' then '自营' when driver_type is null then '' else '加盟' end as driverTypeName, supplier_name allianceName, team_name motorcardName, driver_group_name className, order_no orderNo, evaluate driverEvaluateText, memo driverEvaluateRemark, customer_id evaluateCustomerId, class_name memberRankName, customer_name customerName, CASE order_source WHEN 1 THEN '个人' WHEN 2 THEN '渠道' WHEN 3 THEN '机构' WHEN 4 THEN '酒店' END orderSourceName, fact_date actualAboardTime, fact_end_date completeTime, fact_start_addr actualOnboardLocation, fact_end_addr actualDebusLocation, platform_evaluate appScore, platform_evaluate_score appEvaluateText, platform_memo appRemark, platform osName, sys_version osVersion, superintend_no supervisorNo FROM ${tableName}
AND driver_group_id = #{classId}
AND driver_type = #{driverTypeId}
AND evaluate_score = #{driverScore}
AND platform_evaluate = #{appScore}
and supplier_id = #{allianceId}
and supplier_id in
#{item}
and supplier_id = #{allianceId}
and team_id = #{motorcadeId}
and team_id in
#{item}
and team_id = #{motorcadeId}
AND order_city_id = #{orderCityId}
AND order_city_id in
#{id}
AND order_city_id = #{orderCityId}
ORDER BY fact_end_date ASC
