mapper.select:
SELECT ca.id AS appraisalId, ca.order_no AS orderNo, ca.create_at AS createDate, ca.evaluate_score AS evaluateScore, ca.evaluate AS evaluate, ca.memo AS memo, ca.driver_id AS driverId, ca.driver_name AS driverName, ca.driver_phone AS driverPhone, ca.license_plates AS licensePlates, ca.appraisal_status AS appraisalStatus, ca.order_finish_time as orderFinishTime, ca.is_allowed_appeal as isAllowedAppeal, ca.city_id AS cityId FROM customer_appraisal ca WHERE ca.create_at <= NOW()
and ca.id >= #{minId}
AND ca.id <= #{maxId}
AND ca.appraisal_status = #{appraisalStatus}
AND ca.evaluate_score in (${evaluateScore})
AND ca.create_at >= #{createDateBegin}
AND ca.create_at <= #{createDateEnd}
AND ca.order_finish_time >= #{orderFinishTimeBegin}
AND ca.order_finish_time <= #{orderFinishTimeEnd}
AND ca.order_no LIKE CONCAT(#{orderNo}, '%')
AND ca.order_no in (${orderNos})
AND ca.driver_name LIKE CONCAT(#{driverName}, '%')
AND ca.driver_phone LIKE CONCAT(#{driverPhone}, '%')
AND ca.driver_id in (${driverIds})
AND ca.license_plates LIKE CONCAT(#{licensePlates}, '%')
and ca.is_allowed_appeal = #{isAllowedAppeal}
and ca.is_already_appeal=#{isAlreadyAppeal}
and ca.id in
#{IdItem}
and ca.city_id = #{cityId}
and ca.city_id in (${cities})
and ca.supplier_id = #{supplierId}
and ca.supplier_id in (${suppliers})
ORDER BY ca.create_at,ca.id DESC
ORDER BY ${sortName},ca.id ${sortOrder}

select
id
from customer_appraisal


    and id  >= #{minId}


    and id  <= #{maxId}


  AND appraisal_status = #{appraisalStatus}


    AND evaluate_score in (${evaluateScore})


    AND create_at >= #{createDateBegin}


    AND create_at <= #{createDateEnd}


    AND create_at <= NOW()


    AND order_finish_time >= #{orderFinishTimeBegin}


    AND order_finish_time <= #{orderFinishTimeEnd}



    AND order_no LIKE CONCAT(#{orderNo}, '%')


    AND order_no in (${orderNos})


    AND driver_name LIKE CONCAT(#{driverName}, '%')


    AND driver_phone LIKE CONCAT(#{driverPhone}, '%')


    AND driver_id in (${driverIds})


    AND license_plates LIKE CONCAT(#{licensePlates}, '%')


    and is_allowed_appeal = #{isAllowedAppeal}


    and is_already_appeal=#{isAlreadyAppeal}



    and city_id = #{cityId}


    and city_id in (${cities})


    and supplier_id = #{supplierId}


    and supplier_id in (${suppliers})


    ORDER BY
    create_at,id DESC


    ORDER BY
    ${sortName},id ${sortOrder}
