mapper.insert:
insert into car_relate_team (relation_id, team_id, driver_id
  )
values (#{relationId,jdbcType=INTEGER}, #{teamId,jdbcType=INTEGER}, #{driverId,jdbcType=INTEGER}
  )

mapper.update:
update car_relate_team
set team_id = #{teamId,jdbcType=INTEGER},
  driver_id = #{driverId,jdbcType=INTEGER}
where relation_id = #{relationId,jdbcType=INTEGER}

mapper.select:
SELECT relation_id AS relationId, team_id AS teamId, driver_id AS driverId FROM car_relate_team
AND relation_id = #{relationId}
AND team_id in (${teamId})
AND driver_id = #{driverId}

SELECT t.relation_id AS relationId, t.team_id AS teamId, t.driver_id AS driverId, t1.team_name teamName FROM car_relate_team t,car_driver_team t1 WHERE t.team_id = t1.id
AND relation_id = #{relationId}
AND team_id in (${teamId})
AND driver_id = #{driverId}
limit 1;

SELECT t.relation_id AS relationId, t.group_id AS groupId, t.driver_id AS driverId, t1.team_name groupName FROM car_relate_group t,car_driver_team t1 WHERE t.group_id = t1.id
AND t.relation_id = #{relationId}
AND t.group_id in (${groupId})
AND t.driver_id = #{driverId} limit 1;
