SELECT i.driver_id as driverId, i.account_bank as accountBank, i.age as age, i.archives_no as archivesNo, i.attachment_addr as attachmentAddr, i.attachment_name as attachmentName, i.bank_account_no as bankAccountNo, i.create_by as createBy, i.create_date as createDate, i.credit_card_no as creditCardNo, i.update_by as updateBy, i.update_date as updateDate, i.credit_card_period_of_validity as creditCardPeriodOfValidity, i.credit_CVN2 as CVN2, i.credit_open_account_bank as creditOpenAccountBank, i.current_address as currentAddress, i.driving_license_type as drivingLicenseType, i.driving_years as drivingYears, i.emergency_contact_number as emergencyContactNumber, i.emergency_contact_person as emergencyContactPerson, DATE_FORMAT(i.expire_date,'%Y-%m-%d %T')as expireDate, i.gender as gender, i.id_card_no as idCardNo, DATE_FORMAT(i.issue_date,'%Y-%m-%d %T')as issueDate, i.is_binding_credit_card as isBindingCreditCard, i.license_plates as licensePlates, i.memo as memo, i.`name` as name, i.`password` as password, i.phone as phone, i.photoSrct as photoSrc, i.push_id as pushId, i.service_city as serviceCityId, i.`status` as status, i.storable_card_no as shortCardNo, i.supplier_id as supplierId, i.superintend_no AS superintendNo, i.superintend_url AS superintendUrl, i.service_city as cityId, i.driverLicenseNumber driverLicenseNumber, i.drivingLicenseImg drivingLicenseImg, i.firstDrivingLicenseDate firstDrivingLicenseDate, i.firstMeshworkDrivingLicenseDate firstMeshworkDrivingLicenseDate, i.nationality nationAlity, i.householdRegister houseHoldRegister, i.nation nation, i.marriage marriage, i.foreignLanguage foreignLanguage, i.education education, i.courseName courseName, i.courseDate courseDate, i.courseDateStart courseDateStart, i.courseDateEnd courseDateEnd, i.courseTime courseTime, i.corpType corpType, i.trafficViolations trafficViolations, i.signDate signDate, i.signDateEnd signDateEnd, i.contractDate contractDate, i.isXyDriver isXyDriver, i.xy_driver_number xyDriverNumber, i.partTimeJobDri partTimeJobDri, i.phoneType phoneType, i.phoneCorp phoneCorp, i.appVersion, i.mapType mapType, i.emergencyContactAddr emergencyContactAddr, i.assessment assessment, i.driverLicenseIssuingDateStart driverLicenseIssuingDateStart, i.driverLicenseIssuingDateEnd driverLicenseIssuingDateEnd, i.driverLicenseIssuingCorp driverLicenseIssuingCorp, i.driverLicenseIssuingNumber driverLicenseIssuingNumber, i.driver_license_issuing_register_date driverLicenseIssuingRegisterDate, i.driver_license_issuing_first_date driverLicenseIssuingFirstDate, i.driver_license_issuing_grant_date driverLicenseIssuingGrantDate, i.birth_day birthDay, i.house_hold_register_permanent houseHoldRegisterPermanent, i.group_id as groupId, i.cooperation_type cooperationType, DATE_FORMAT(i.create_date,'%Y-%m-%d %H:%i:%s') createDateStr, DATE_FORMAT(i.update_date,'%Y-%m-%d %H:%i:%s') updateDateStr, ci.car_id as licensePlatesId, dt.team_name teamName, dg.team_name groupName, d.user_name createByName, e.user_name updateByName FROM car_biz_driver_info_temp i left join car_biz_car_info_temp ci on ci.license_plates=i.license_plates left join car_driver_team dt on dt.id=i.team_id left join car_driver_team dg on dg.id=i.team_group_id left join car_adm_user d on d.user_id=i.create_by left join car_adm_user e on e.user_id=i.update_by where 1=1
and i.license_plates LIKE '%${licensePlates}%'
and i.name LIKE '%${name}%'
and i.phone LIKE '%${phone}%'
and i.service_city = #{serviceCityId}
and i.service_city in (${cities})
and i.supplier_Id = #{supplierId}
and i.supplier_id in (${suppliers})
and i.team_id in (${teamIds})
and i.team_group_id in (${groupIds})
and i.group_id = #{groupid}
and i.create_date >= #{createDateBegin}
and i.create_date <= #{createDateEnd}
order by i.update_date desc LIMIT ${offSet},${pagesize}

