mapper.select:
SELECT p.id, p.tips_title, p.tips_content, u.user_name,IFNULL(GROUP_CONCAT(DISTINCT d.doc_name),'') as doc_name, p.create_time, p.update_time,p.user_id FROM car_biz_supplier_tips p LEFT JOIN car_biz_tips_doc d on p.id = d.tips_id INNER JOIN car_adm_user u on p.user_id = u.user_id WHERE p.status = 1
AND p.create_time >=#{startDate}
AND p.create_time <= #{endDate}
AND (LOCATE(#{keyword},p.tips_title) > 0 OR p.id in (SELECT tips_id FROM car_biz_tips_doc WHERE LOCATE(#{keyword}, doc_name ) > 0 GROUP BY tips_id) )
AND p.user_id in
#{item}
GROUP BY p.id,tips_title,p.update_time,tips_content ORDER BY p.update_time desc

SELECT p.id, p.tips_title, p.tips_content, u.user_name,IFNULL(GROUP_CONCAT(DISTINCT d.doc_name),'') as doc_name, p.create_time, p.update_time,p.user_id FROM car_biz_supplier_tips p LEFT JOIN car_biz_tips_doc d on p.id = d.tips_id INNER JOIN car_adm_user u on p.user_id = u.user_id WHERE p.status = 1
AND p.create_time >=#{startDate}
AND p.create_time <= #{endDate}
AND LOCATE(#{keyword},p.tips_title) > 0
AND p.user_id in
#{item}
GROUP BY p.id,tips_title,p.update_time,tips_content ORDER BY p.update_time desc

SELECT p.id, p.tips_title, p.tips_content, u.user_name,IFNULL(GROUP_CONCAT(DISTINCT d.doc_name),'') as doc_name, p.create_time, p.update_time,p.user_id FROM car_biz_supplier_tips p LEFT JOIN car_biz_tips_doc d on p.id = d.tips_id INNER JOIN car_adm_user u on p.user_id = u.user_id WHERE p.status = 1
AND p.create_time >=#{startDate}
AND p.create_time <= #{endDate}
AND p.id in (SELECT tips_id FROM car_biz_tips_doc WHERE LOCATE(#{keyword}, doc_name ) > 0 GROUP BY tips_id)
AND p.user_id in
#{item}
GROUP BY p.id,tips_title,p.update_time,tips_content ORDER BY p.update_time desc
