mapper.select:
select
COUNT (1)
from
car_driver_day_duty d
where 1=1

  and d.driver_id = #{driverId}


  and d.time = #{time}


  and d.city_id = #{cityId}


  and d.supplier_id = #{supplierId}


  AND d.team_id = #{teamId}


  and d.status = #{status}


  and city IN

    #{cid}



  and supplier IN

    #{sid}



  and id IN

    #{tid}



  and d.driver_name = #{driverName}


  AND d.time >= '${startTime}' 


  AND d.time <= '${endTime}' 


  ORDER BY ${sortname} ${sortorder}


  ORDER BY d.time desc

select d.id, d.driver_id driverId, d.driver_name driverName, d.city_id cityId, d.city_name cityName, d.supplier_id supplierId, d.supplier_name supplierName, d.team_id teamId, d.team_name teamName, d.time, d.forced_ids forcedIds, d.duty_ids dutyIds, d.forced_times forcedTimes, d.duty_times dutyTimes, d.type, d.status from car_driver_day_duty d where 1=1
and d.driver_id = #{driverId}
and d.time = #{time}
and d.city_id = #{cityId}
and d.supplier_id = #{supplierId}
AND d.team_id = #{teamId}
and d.status = #{status}
and city IN
#{cid}
and supplier IN
#{sid}
and id IN
#{tid}
and d.driver_name = #{driverName}
AND d.time >= '${startTime}'
AND d.time <= '${endTime}'
ORDER BY ${sortname} ${sortorder}
ORDER BY d.time desc

    select

    from car_driver_day_duty d
    where 1=1

      and d.driver_id = #{driverId}


      and d.driver_name = #{driverName}



			and d.startTime >= #{startTime, jdbcType=TIMESTAMP}




			and d.endTime <= #{endTime, jdbcType=TIMESTAMP}



      and d.time = #{time}


      and d.city_id IN

        #{cid}



      and d.supplier_id IN

        #{sid}



      and d.team_id IN

        #{tid}


    order by d.city_id, d.id

update.foreach:
UPDATE car_driver_day_duty
driver_name = #{item.driverName}, city_id = #{item.cityId}, city_name = #{item.cityName}, supplier_id = #{item.supplierId}, supplier_name = #{item.supplierName}, team_id = #{item.teamId}, team_name = #{item.teamName}, forced_ids = #{item.forcedIds}, duty_ids = #{item.dutyIds}, forced_times = #{item.forcedTimes}, duty_times = #{item.dutyTimes}, type = #{item.type}, status = #{item.status}
WHERE id = #{item.id}
