mapper.select:
select d.id, d.city, d.city_name cityName, d.supplier, d.supplier_name supplierName, d.duty_name dutyName, d.startDate, d.endDate, d.create_by createBy, d.create_date createDate, d.update_by updateBy, d.update_date updateDate, d.remark, d.status, CONCAT(startDate,'-',endDate) as times from car_duty_duration d where 1=1
and d.city = #{cityId}
and d.supplier = #{supplierId}
and d.team_id = #{teamId}
and d.status = #{status}
order by d.update_date desc

select d.id, d.city, d.city_name cityName, d.supplier, d.supplier_name supplierName, d.duty_name dutyName, d.startDate, d.endDate, d.create_by createBy, d.create_date createDate, d.update_by updateBy, d.update_date updateDate, d.remark, d.status, u1.user_name createName, u2.user_name updateName, t3.team_name teamName, d.team_id teamId from car_duty_duration d left join car_adm_user u1 on d.create_by=u1.user_id left join car_adm_user u2 on d.update_by=u2.user_id left JOIN car_driver_team t3 on d.team_id = t3.id where 1=1
and d.id in (${dutyIds})
and d.city_id IN
#{cid}
and d.supplier_id IN
#{sid}
and d.team_id IN
#{tid}
and d.status = #{status}
order by d.update_date desc
