mapper.select:
select p.id, p.mesage_title, p.message_content, u.user_name,GROUP_CONCAT(d.doc_name) as doc_name, p.create_time, p.update_time FROM car_message_post p LEFT JOIN car_message_receiver r on p.id = r.message_id LEFT JOIN car_message_doc d on p.id = d.message_id INNER JOIN car_adm_user u on p.user_id = u.user_id WHERE 1=1
AND r.receive_user_id = #{userId}
AND r.status = #{status} group BY p.id ORDER BY p.create_time DESC

select p.id, p.mesage_title, p.message_content, u.user_name,GROUP_CONCAT(d.doc_name) as doc_name, p.create_time, p.update_time FROM car_message_post p LEFT JOIN car_message_doc d on p.id = d.message_id INNER JOIN car_adm_user u on p.user_id = u.user_id WHERE 1=1
AND p.status = #{status}
AND p.user_id = #{userId} group BY p.id ORDER BY p.create_time DESC
