mapper.select:
select

from car_message_receiver
where 1=1


  AND message_id = #{messageId,jdbcType=INTEGER}



  AND receive_user_id = #{receiveUserId,jdbcType=INTEGER}


  AND status = #{status,jdbcType=INTEGER}

ORDER BY update_time DESC

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into car_message_receiver (status, receive_user_id, message_id,
create_time, update_time, is_sender
)
values (#{status,jdbcType=INTEGER}, #{receiveUserId,jdbcType=INTEGER}, #{messageId,jdbcType=INTEGER},
#{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{isSender,jdbcType=VARCHAR}
)

mapper.update:
update car_message_receiver
 set status = 1, update_time = now()
where id = #{id,jdbcType=BIGINT}

update car_message_receiver
set status = #{status}, update_time = now()
where receive_user_id = #{receiveUserId}
and message_id = #{messageId}

mapper.select:
SELECT cdu.user_name as user_name, cmp.user_id as create_uid, cmr.receive_user_id as receive_user_id, cmp.id as id, cmp.mesage_title as mesage_title, cmr.status as status, cmp.create_time as create_time, cmp.message_content as message_content, cmp.status as message_status, IFNULL(GROUP_CONCAT(DISTINCT cmd.doc_name),'') as docName FROM car_message_post cmp LEFT JOIN car_message_receiver cmr ON cmp.id = cmr.message_id INNER JOIN car_adm_user cdu ON cmp.user_id = cdu.user_id LEFT JOIN car_message_doc cmd ON cmd.message_id = cmp.id WHERE 1=1
AND cmp.status = #{status}
AND cmp.create_time >=DATE_FORMAT(#{startDate},'%Y%m%d')
AND cmp.create_time <= DATE_FORMAT(#{endDate},'%Y%m%d')
AND LOCATE(#{keyword},cmp.mesage_title) > 0
AND cmp.user_id in
#{item}
AND cmp.user_id=#{userId}
GROUP BY cmp.id,mesage_title,cmp.create_time,message_content

select COUNT(id)
from car_message_receiver
where receive_user_id = #{receiveUserId,jdbcType=BIGINT} AND message_id = #{messageId}

SELECT cdu.user_name as user_name, cmp.user_id as create_uid, cmr.receive_user_id as receive_user_id, cmp.id as id, cmp.mesage_title as mesage_title, cmr.status as status, cmp.create_time as create_time, cmp.message_content as message_content, cmp.status as message_status, IFNULL(GROUP_CONCAT(cmd.doc_name),'') as docName, r.reply_content FROM car_message_post cmp LEFT JOIN car_message_receiver cmr ON cmp.id = cmr.message_id INNER JOIN car_adm_user cdu ON cmp.user_id = cdu.user_id LEFT JOIN car_message_doc cmd ON cmd.message_id = cmp.id LEFT JOIN car_message_reply r on cmr.id = r.message_id
WHERE 1=1
AND cmp.status = #{status}
AND cmp.create_time >=STR_TO_DATE(#{startDate},'%Y-%m-%d')
AND cmp.create_time <= STR_TO_DATE(#{endDate},'%Y-%m-%d')
AND LOCATE(#{keyword},cmp.mesage_title) > 0
AND cmp.user_id in
#{item}
AND cmr.receive_user_id=#{userId}
GROUP BY cmp.id,cmp.mesage_title, cmp.create_time,cmp.message_content
