mapper.delete:
delete from car_relate_team
WHERE
 team_id = #{paramId}
  AND driver_id = #{driverId}

mapper.select:
select rt.relation_id AS relationId, t.team_name teamName, rt.driver_id driverId, rt.team_id teamId from car_relate_team rt LEFT JOIN car_driver_team t ON rt.team_id = t.id where rt.driver_id = #{driverId,jdbcType=INTEGER}
AND rt.team_id = #{teamId}
limit 1

