mapper.select:
    select 
    
    from saas_permission
    where 1=1
    
    and permission_id in 
    
	    #{pid}
    
    
    
    and parent_permission_id = #{parentId}
    
    
    and permission_code = #{permissionCode}
    
    
    and permission_type in 
    
	    #{type}
    
    
    
    and permission_name like #{permissionName}
    
    
    and valid = #{valid}
    
    order by sort_seq , permission_id
