mapper.select:
SELECT
	car_id carId,
	license_plates licensePlates
FROM
car_biz_car_info
WHERE driver_id = '0' and status=1

	and city_id = #{cityId}


	and supplier_id = #{supplierId}


	and license_plates like '${licensePlates}%'


	or driver_id = #{driverId}

SELECT count(*)
FROM
	car_biz_car_info
WHERE license_plates = #{licensePlates}
and city_id = #{cityId}
and supplier_id = #{supplierId}

SELECT c.car_id AS carId, c.license_plates AS licensePlates, c.color AS color, p.supplier_full_name AS supplierName, g.group_name AS groupName FROM car_biz_car_info c LEFT JOIN car_biz_supplier p ON c.supplier_id = p.supplier_id LEFT JOIN car_biz_model m ON m.model_id = c.car_model_id LEFT JOIN car_biz_car_group g ON m.group_id = g.group_id WHERE c.STATUS = 1 AND g.type = 2 AND car_id != 0
and c.license_plates LIKE CONCAT('%',#{licensePlates},'%')
and c.license_plates not in
#{item}
and g.seat_num >= #{seatNum}
and c.city_id = #{cityId}
and c.supplier_id = #{supplierId}
and c.supplier_id in
#{item}
order by c.update_date desc
