SELECT id, cooperation_name cooperationName, status, create_by createBy, update_by updateBy, create_name createName, update_name updateName, create_date createDate, update_date updateDate, DATE_FORMAT(create_date, '%Y-%m-%d %H:%i:%s') createDateStr, DATE_FORMAT(update_date, '%Y-%m-%d %H:%i:%s') updateDateStr FROM car_biz_cooperation_type WHERE 1=1
and id = #{id}
and cooperation_name like '${cooperationName}%'
and status = #{status}

SELECT id, cooperation_name cooperationName, status, create_by createBy, update_by updateBy, create_name createName, update_name updateName, create_date createDate, update_date updateDate, DATE_FORMAT(create_date, '%Y-%m-%d %H:%i:%s') createDateStr, DATE_FORMAT(update_date, '%Y-%m-%d %H:%i:%s') updateDateStr FROM car_biz_cooperation_type WHERE 1=1
and id = #{id}
and cooperation_name like '${cooperationName}%'
and status = #{status}
order by update_date desc limit #{offSet},#{pagesize}

SELECT count(*) FROM car_biz_cooperation_type WHERE 1=1
and id = #{id}
and cooperation_name like '${cooperationName}%'
and status = #{status}

