SELECT o.id outageId, o.driver_id driverId, o.out_start_date outStartDate, o.out_stop_long_time outStopLongTime, o.out_end_date outEndDate, o.fact_start_date factStartDate, o.fact_end_date factEndDate, o.outage_source outageSource, o.outage_reason outageReason, o.create_by createBy, o.create_name createName, o.create_date createDate, o.remove_by removeBy, o.remove_name removeName, o.remove_date removeDate, o.remove_reason removeReason, o.remove_status removeStatus, o.order_nos orderNos, i.name driverName, i.phone driverPhone, i.license_plates licensePlates, c.city_id cityId, c.city_name cityName, p.supplier_id supplierId, p.supplier_full_name supplierName, g.group_id carGroupId, g.group_name carGroupName, DATE_FORMAT(o.out_start_date, '%Y-%m-%d %H:%i:%s') outStartDateStr, DATE_FORMAT(o.out_end_date, '%Y-%m-%d %H:%i:%s') outEndDateStr, DATE_FORMAT(o.fact_start_date, '%Y-%m-%d %H:%i:%s') factStartDateStr, DATE_FORMAT(o.fact_end_date, '%Y-%m-%d %H:%i:%s') factEndDateStr, DATE_FORMAT(o.create_date, '%Y-%m-%d %H:%i:%s') createDateStr, DATE_FORMAT(o.remove_date, '%Y-%m-%d %H:%i:%s') removeDateStr FROM car_biz_driver_outage o LEFT JOIN car_biz_driver_info i on i.driver_id = o.driver_id LEFT JOIN car_biz_city c ON c.city_id = i.service_city LEFT JOIN car_biz_supplier p ON i.supplier_id = p.supplier_id LEFT JOIN car_biz_car_group g ON i.group_id = g.group_id WHERE 1=1
and o.outage_source = #{outageSource}
and o.remove_status = #{removeStatus}
and i.name like '%${driverName}%'
and i.phone like '%${driverPhone}%'
and c.city_id = #{cityId}
and c.city_id in (${cities})
and p.supplier_id = #{supplierId}
and p.supplier_id in (${supplierIds})
and g.group_id = #{carGroupId}
AND o.out_start_date >= '${startDateBegin} 00:00:00'
AND o.out_start_date<= '${startDateEnd} 23:59:59'
ORDER BY o.create_date desc LIMIT ${offSet},${pagesize}

